import React, {Component} from "react";
import {GuestGreeting, UserGreeting} from "./Greeting_users";
import {LogoutButton, LoginButton} from "./AuthButtons";

class LoginControl extends Component {
    constructor(props) {
        super(props);
        this.state = {isLoggedIn: false}
        this.handleLoginClick = this.handleLoginClick.bind(this);
        this.handleLogoutClick = this.handleLogoutClick.bind(this);
    }

    handleLoginClick() {
        this.setState({isLoggedIn: true})
    }

    handleLogoutClick() {
        this.setState({isLoggedIn: false})
    }

    render() {
        return (
            <div>
                {this.state.isLoggedIn
                    ? [<LogoutButton onClick={this.handleLogoutClick}/>, <UserGreeting/>]
                    : [<LoginButton onClick={this.handleLoginClick}/>, <GuestGreeting/>]
                }
            </div>
        )
    }
}

export default LoginControl
